<?php


function parseFloors($input){
    $curFloor = 0;
    for($x = 0; $x < strlen($input); $x++){
        $instruction = substr($input,$x,1);
        if($instruction == '('){
            $curFloor++;
            echo "Instruction: $instruction Moving up\n";
        }else{
            $curFloor--;
            echo "Instruction: $instruction Moving down\n";
        }
        if($curFloor == -1){
            echo "Santa entered basement at pos ".$x."\n";
            break;
        }
    }
}
